import React, { useRef } from "react";
import PropTypes from 'prop-types';

const CategoryDropdown = ({ categories }) => {
  const formRef = useRef(null);

  const handleChange = async (event) => {
    event.preventDefault();
    const selectedCategory = event.target.value;
    console.log(`Selected Category: ${selectedCategory}`)

    try {
      const query = new URLSearchParams({ category: selectedCategory || '' }).toString()
      console.log(`Query: ${query}`)
      const response = await fetch(`/api/products/category?${query}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      };

      const text = response.text;
      console.log(`cd.js Response Text: ${text}`)

      if (text) {
        const data = JSON.parse(text);
        console.log(`cd.js Response Data: ${data}`);
      } else {
        console.log("Response Body Empty")
      };
    } catch (error) {
      console.error(error.message);
    };
  };

  const renderOptions = () => {
    const uniqueCategories = [...new Set(categories)].sort();
    return uniqueCategories.map((category) => (
      <option key={category} value={category}>
        {category}
      </option>
    ));
  };

  return (
    <form ref={formRef} onSubmit={handleChange}>
      <select
        onChange={handleChange}
        id="category-select"
        className="xl:w-52 lg:w-40 md:w-32 w-28 pl-2 py-1.5 rounded-l-md"
        aria-label="Category Select"
      >
        <option value="0">Categories</option>
        {renderOptions()}
      </select>
    </form>
  );
};

CategoryDropdown.propTypes = {
  categories: PropTypes.arrayOf(PropTypes.string).isRequired,
};

export default CategoryDropdown;
