import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductsList = () => {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetch_products = async () => {
            try {
                const response = await axios.get('/api/products/all');

                if (response && response.data && typeof(response.data) === 'object') {
                    const flatProducts = Object.values(response.data);

                    // for (let product in flatProducts) {
                    //     for (let product_info_item in product) {
                    //         products.push(product)
                    // }}


                    let product_info = [];
                    let products = []
                    const num_products = flatProducts.length;
                    for (let i = 0; i < num_products; i++) {
                        console.log(i)
                        let product = flatProducts[i][1];
                        product_info.push([product.productName, product.productDescription, product.productCoverImage, product.dateUploaded])
                        products.push(product_info)
                        // console.log(product_info)
                        // console.log(products)
                    };
                    console.log(`Product Info: ${product_info}`)
                    console.log(`All Products: ${products}`)


                    // console.log("test")
                    // console.log(products)

                    // const product = flatProducts[0][1];
                    // const product_info = [product.productName, product.productDescription, product.productCoverImage, product.dateUploaded];
                    // console.log(product_info)                    
                    setProducts(products);
                    }
            } catch (error) {
                setError(error.message);
            }
        };

        fetch_products();
    }, []);

    return (
        <div id='products-wrapper' className='relative flex flex-row flex-wrap gap-4 px-4 bg-green-500'>
            <h1 className='absolute left-1/2'>Product List</h1>
            {error && <p>Error: {error}</p>}

            {products.map((product_info, index) =>
            <a href='#' className='w-1/4 flex flex-col p-2 border border-black rounded-md mt-6 dark:bg-darkest-grey dark:hover:scale-105 transition-all'>
                <ul key={index} id='product-container'>
                    <li className='h-2/3 w-full'>
                        <img src={product_info[index][2]} className='h-full w-full border border-black'></img>
                    </li>
                    <li>Name: {product_info[index][0]}</li>
                    <li>Description {product_info[index][1]}</li>
                    <li>Date Created: {product_info[index][3]}</li>
                </ul>
            </a>
            )}

        </div>
    )
}

export default ProductsList;
