import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';

const ProductsListTitle = ({ category }) => {
    const title = category || "All";
    return <h1 className='mx-auto text-4xl z-10'>{title} Products</h1>;
};

const ProductsList = ({ category }) => {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetch_products = async () => {
            try {
                const query = new URLSearchParams({ category: category || '' });
                const response = await fetch(`/api/products/category?${query}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        },
                    });
                console.log(`Reponse Data: ${typeof(response.data)}`)

                // if (response && response.data && Array.isArray(response.data)) {
                //     setProducts(response.data);
                // } else {
                //     throw new Error("Invalid Response Data");
                // };

                // const response = await axios.get('/api/products/all');

                if (response && response.data && typeof(response.data) === 'object') {
                    const flatProducts = Object.values(response.data);

                    let product_info = [];
                    let products = []
                    const num_products = flatProducts.length;
                    for (let i = 0; i < num_products; i++) {
                        let product = flatProducts[i][1];
                        product_info.push([product.productName, product.productDescription, product.productCoverImage, product.dateUploaded])
                        products.push(product_info)
                    };
                 
                    setProducts(products);
                    }
            } catch (error) {
                console.log(error.message);
            }
        };

        fetch_products();
    }, [category]);

    return (
        <div id='products-section-wrapper' className='flex-grow flex-shrink flex flex-col bg-darkest-grey'>
            <div className='relative flex flex-row'>
                <Link className='absolute self-center p-2 rounded-full ml-4 bg-blue-500 text-center' to="/create-product">
                    Upload+
                </Link>

                {ProductsListTitle(category)}
            </div>

            {error && <p>Error: {error}</p>}
            <div className='h-full relative flex flex-row flex-auto flex-wrap gap-4 px-4 pb-4 bg-darkest-grey'>
    {products ? (
        products.map((product_info, index) => (
            <a
                href='#'
                key={index}
                className='h-72 w-1/4 min-w-28 relative flex flex-col p-2 rounded-md mt-6 dark:bg-darker-grey dark:bg-opacity-60 dark:hover:scale-105 transition-all'
            >
                <div className='h-full w-full'>
                    <div className='h-2/3 w-full'>
                        <img
                            src={product_info[2]}
                            alt={`Product ${index}`}
                            className='border aspect-video'
                        />
                    </div>
                    <div className='mt-6'>
                        <p>{product_info[0]}</p>
                        <p className='text-sm'>Description: {product_info[1]}</p>
                    </div>
                </div>
            </a>
        ))
    ) : (
        <h1>No Products</h1>
    )}
</div>

        </div>
    )
}

export default ProductsList;
