import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function ProductsListTitle({ category }) {
    const title = category || "All Products";
    return (
        <h1 className='mx-auto text-4xl z-10'>{title}</h1>
    )
}

const ProductsList = ({ category }) => {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetch_products = async () => {
            try {
                const query = new URLSearchParams({ category: category || '' }).toString();
                console.log(`Query: ${query}`);
                console.log(`Category: ${category}`);

                const response = await fetch(`/api/products/category?${query}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error(`Network Response not ok: ${response.statusText}`);
                }

                const data = await response.json();
                console.log(`Data: ${data}`);
                console.log(`Data Type: ${typeof(data)}`);

                if (data && typeof(data) === "object") {
                    const flat_products = Object.values(data);
                    console.log(`Flat Products: ${flat_products}`)

                    let product_info = [];
                    let products = [];
                    const num_products = flat_products.length;
                    console.log(num_products)

                    for (let i; i < num_products; i++) {
                        console.log(i)
                        let product = flat_products[i][1];
                        product_info.push(product.productName, product.productCategory, product.productDescription, product.productCoverImage, product.dateUploadeda)
                        products.push(product_info)
                    };
                    console.log(`Product Info: ${product_info}`)
                    console.log(`Products: ${products}`)

                    setProducts(products);
                }
            } catch (error) {
                console.error('Fetching products failed:', error);
                setError(error.message);
            }
        };

        fetch_products();
    }, [category]);

    return (
        <div id='products-section-wrapper' className='flex-grow flex-shrink flex flex-col bg-darkest-grey'>
            <div className='relative flex flex-row'>
                <Link className='absolute self-center p-2 rounded-full ml-4 bg-blue-500 text-center' to="/create-product">
                    Upload+
                </Link>
                <ProductsListTitle category={category} />
            </div>

            {error && <p>Error: {error}</p>}
            <div className='h-full relative flex flex-row flex-auto flex-wrap gap-4 px-4 pb-4 bg-darkest-grey'>
                {products.map((product, index) => (
                    <a key={index} href='#' className='h-72 h-auto w-1/4 min-w-28 relative flex flex-col p-2 rounded-md mt-6 dark:bg-darker-grey dark:bg-opacity-60 dark:hover:scale-105 transition-all'>
                        <ul id='product-container' className='h-full w-full'>
                            <li className='h-2/3 w-full'>
                                <img src={product.productCoverImage} className='border aspect-video' alt={`${product.productName} cover`} />
                            </li>
                            <div className='mt-6'>
                                <li>{product.productName}</li>
                                <li className='text-sm'>Description: {product.productDescription}</li>
                            </div>
                        </ul>
                    </a>
                ))}
            </div>
        </div>
    );
};

export default ProductsList;
