import React from "react";
import categories from '../utils/categories';
import CategoryDropdown from "../utils/category-dropdown";
import searchIconWhite from '../assets/search-icon-white.png'


function Navbar() {
    return (
        <div className="w-2/3 flex flex-row gap-5 items-center m-auto mt-4">
            <a className="absolute left-64 cursor-pointer hover:underline" href="#">About</a>
            <div className="absolute left-96 flex flex-row">
                <CategoryDropdown categories={categories}/>
                <form id="main-search-bar" className="w-96 flex flex-row mx-auto">
                    <input name="search-bar-query" className="w-2/3 px-2 py-2 border-0 rounded-none outline-blue-500 md:dark:bg-gray-800" type="text" placeholder="Search for a product..."></input>
                    <button name="search-bar-btn" className="w-24 flex items-center justify-center rounded-r-full bg-blue-500 hover:bg-blue-700 transition-all" type="submit">
                        <div className="w-8 relative">
                            <img src={searchIconWhite} alt="Search Icon"></img>
                        </div>
                    </button>
                </form>
            </div>
            <a className="absolute right-24 p-2 rounded-full hover:bg-gray-800" href="#">Basket</a>
            <a className="absolute right-4 hover:underline" href="#">Profile</a>
        </div>
    )
}

export default Navbar;
