import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductsList = () => {
    const [product_info, set_product_info] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetch_products = async () => {
            try {
                const response = await axios.get('/api/products/all');

                if (response && response.data && typeof(response.data) === 'object') {
                    const flatProducts = Object.values(response.data);
                    let products = [];

                    // for (let product in flatProducts) {
                    //     for (let product_info_item in product) {
                    //         products.push(product)
                    // }}

                    const num_products = flatProducts.length;
                    for (let i = 0; i < num_products; i++) {
                        console.log(flatProducts[i][1]);
                        // products.push(flatProducts[i][1])
                    };


                    // console.log("test")
                    // console.log(products)

                    const product = flatProducts[0][1];
                    const product_info = [product.productName, product.productDescription, product.productCoverImage, product.dateUploaded];
                    // console.log(product_info)                    
                    set_product_info(product_info);
                    }
            } catch (error) {
                setError(error.message);
            }
        };

        fetch_products();
    }, []);

    return (
        <div className='bg-green-500'>
            <h1>Product List</h1>
            {error && <p>Error: {error}</p>}
            <ul>
                <li>Name: {product_info[0]}</li>
                <li>Description: {product_info[1]}</li>
                <li>Date Created: {product_info[3]}</li>
                <img src={product_info[2]} className='border-1 border-red-500'></img>
            </ul>
        </div>
    )
}

export default ProductsList;
